var BufferAttributeModule = require('./BufferAttribute');
var DynamicBufferAttribute;
DynamicBufferAttribute = function (array, itemSize) {
	BufferAttributeModule.BufferAttribute.call(this, array, itemSize);
	this.updateRange = {
		offset: 0,
		count: -1
	};
};
DynamicBufferAttribute.prototype = Object.create(BufferAttributeModule.BufferAttribute.prototype);
DynamicBufferAttribute.prototype.constructor = DynamicBufferAttribute;
DynamicBufferAttribute.prototype.clone = function () {
	return new DynamicBufferAttribute(new this.array.constructor(this.array), this.itemSize);
};
DynamicBufferAttribute.prototype.isDynamicBufferAttribute = true;
exports.DynamicBufferAttribute = DynamicBufferAttribute;