/**************************************************************
 *	Line3D
 **************************************************************/
var Vector3Module = require('../../math/Vector3');
var CurveModule = require('../core/Curve');
exports.LineCurve3 = CurveModule.Curve.create(function (v1, v2) {
	this.v1 = v1;
	this.v2 = v2;
}, function (t) {
	var vector = new Vector3Module.Vector3();
	vector.subVectors(this.v2, this.v1);
	// diff
	vector.multiplyScalar(t);
	vector.add(this.v1);
	return vector;
});
if (typeof exports.LineCurve3 === 'function')
	exports.LineCurve3.prototype.isLineCurve3 = true;