/**************************************************************
 *	Quadratic Bezier 3D curve
 **************************************************************/
var ShapeUtilsModule = require('../ShapeUtils');
var Vector3Module = require('../../math/Vector3');
var CurveModule = require('../core/Curve');
exports.QuadraticBezierCurve3 = CurveModule.Curve.create(function (v0, v1, v2) {
	this.v0 = v0;
	this.v1 = v1;
	this.v2 = v2;
}, function (t) {
	var vector = new Vector3Module.Vector3();
	vector.x = ShapeUtilsModule.ShapeUtils.b2(t, this.v0.x, this.v1.x, this.v2.x);
	vector.y = ShapeUtilsModule.ShapeUtils.b2(t, this.v0.y, this.v1.y, this.v2.y);
	vector.z = ShapeUtilsModule.ShapeUtils.b2(t, this.v0.z, this.v1.z, this.v2.z);
	return vector;
});
if (typeof exports.QuadraticBezierCurve3 === 'function')
	exports.QuadraticBezierCurve3.prototype.isQuadraticBezierCurve3 = true;