var BoxGeometryModule = require('./BoxGeometry');
var Three = require('../../Three');
var CubeGeometry;
CubeGeometry = function (width, height, depth, widthSegments, heightSegments, depthSegments) {
	Three.warn('THREE.CubeGeometry has been renamed to THREE.BoxGeometry.');
	return new BoxGeometryModule.BoxGeometry(width, height, depth, widthSegments, heightSegments, depthSegments);
};
CubeGeometry.prototype.isCubeGeometry = true;
exports.CubeGeometry = CubeGeometry;