var LineModule = require('../../objects/Line');
var Three = require('../../Three');
var LineBasicMaterialModule = require('../../materials/LineBasicMaterial');
var BufferAttributeModule = require('../../core/BufferAttribute');
var BufferGeometryModule = require('../../core/BufferGeometry');
var AxisHelper;
AxisHelper = function (size) {
	size = size || 1;
	var vertices = new Float32Array([
		0,
		0,
		0,
		size,
		0,
		0,
		0,
		0,
		0,
		0,
		size,
		0,
		0,
		0,
		0,
		0,
		0,
		size
	]);
	var colors = new Float32Array([
		1,
		0,
		0,
		1,
		0.6,
		0,
		0,
		1,
		0,
		0.6,
		1,
		0,
		0,
		0,
		1,
		0,
		0.6,
		1
	]);
	var geometry = new BufferGeometryModule.BufferGeometry();
	geometry.addAttribute('position', new BufferAttributeModule.BufferAttribute(vertices, 3));
	geometry.addAttribute('color', new BufferAttributeModule.BufferAttribute(colors, 3));
	var material = new LineBasicMaterialModule.LineBasicMaterial({ vertexColors: Three.VertexColors });
	LineModule.Line.call(this, geometry, material, Three.LinePieces);
};
AxisHelper.prototype = Object.create(LineModule.Line.prototype);
AxisHelper.prototype.constructor = AxisHelper;
AxisHelper.prototype.isAxisHelper = true;
exports.AxisHelper = AxisHelper;