var Three = require('../../Three');
var LineBasicMaterialModule = require('../../materials/LineBasicMaterial');
var LineModule = require('../../objects/Line');
var Vector3Module = require('../../math/Vector3');
var GeometryModule = require('../../core/Geometry');
var VertexTangentsHelper;
VertexTangentsHelper = function (object, size, hex, linewidth) {
	this.object = object;
	this.size = size !== undefined ? size : 1;
	var color = hex !== undefined ? hex : 255;
	var width = linewidth !== undefined ? linewidth : 1;
	var geometry = new GeometryModule.Geometry();
	var faces = object.geometry.faces;
	for (var i = 0, l = faces.length; i < l; i++) {
		var face = faces[i];
		for (var j = 0, jl = face.vertexTangents.length; j < jl; j++) {
			geometry.vertices.push(new Vector3Module.Vector3());
			geometry.vertices.push(new Vector3Module.Vector3());
		}
	}
	LineModule.Line.call(this, geometry, new LineBasicMaterialModule.LineBasicMaterial({
		color: color,
		linewidth: width
	}), Three.LinePieces);
	this.matrixAutoUpdate = false;
	this.update();
};
VertexTangentsHelper.prototype = Object.create(LineModule.Line.prototype);
VertexTangentsHelper.prototype.constructor = VertexTangentsHelper;
VertexTangentsHelper.prototype.update = function (object) {
	var v1 = new Vector3Module.Vector3();
	return function (object) {
		var keys = [
			'a',
			'b',
			'c',
			'd'
		];
		this.object.updateMatrixWorld(true);
		var vertices = this.geometry.vertices;
		var verts = this.object.geometry.vertices;
		var faces = this.object.geometry.faces;
		var worldMatrix = this.object.matrixWorld;
		var idx = 0;
		for (var i = 0, l = faces.length; i < l; i++) {
			var face = faces[i];
			for (var j = 0, jl = face.vertexTangents.length; j < jl; j++) {
				var vertexId = face[keys[j]];
				var vertex = verts[vertexId];
				var tangent = face.vertexTangents[j];
				vertices[idx].copy(vertex).applyMatrix4(worldMatrix);
				v1.copy(tangent).transformDirection(worldMatrix).multiplyScalar(this.size);
				v1.add(vertices[idx]);
				idx = idx + 1;
				vertices[idx].copy(v1);
				idx = idx + 1;
			}
		}
		this.geometry.verticesNeedUpdate = true;
		return this;
	};
}();
VertexTangentsHelper.prototype.isVertexTangentsHelper = true;
exports.VertexTangentsHelper = VertexTangentsHelper;