var LightModule = require('./Light');
var AmbientLight;
AmbientLight = function (color) {
	LightModule.Light.call(this, color);
	this.type = 'AmbientLight';
};
AmbientLight.prototype = Object.create(LightModule.Light.prototype);
AmbientLight.prototype.constructor = AmbientLight;
AmbientLight.prototype.clone = function () {
	var light = new AmbientLight();
	LightModule.Light.prototype.clone.call(this, light);
	return light;
};
AmbientLight.prototype.isAmbientLight = true;
exports.AmbientLight = AmbientLight;