var Vector3Module = require('../math/Vector3');
var LightModule = require('./Light');
var AreaLight;
AreaLight = function (color, intensity) {
	LightModule.Light.call(this, color);
	this.type = 'AreaLight';
	this.normal = new Vector3Module.Vector3(0, -1, 0);
	this.right = new Vector3Module.Vector3(1, 0, 0);
	this.intensity = intensity !== undefined ? intensity : 1;
	this.width = 1;
	this.height = 1;
	this.constantAttenuation = 1.5;
	this.linearAttenuation = 0.5;
	this.quadraticAttenuation = 0.1;
};
AreaLight.prototype = Object.create(LightModule.Light.prototype);
AreaLight.prototype.constructor = AreaLight;
AreaLight.prototype.isAreaLight = true;
exports.AreaLight = AreaLight;