var ColorModule = require('../math/Color');
var LightModule = require('./Light');
var HemisphereLight;
HemisphereLight = function (skyColor, groundColor, intensity) {
	LightModule.Light.call(this, skyColor);
	this.type = 'HemisphereLight';
	this.position.set(0, 100, 0);
	this.groundColor = new ColorModule.Color(groundColor);
	this.intensity = intensity !== undefined ? intensity : 1;
};
HemisphereLight.prototype = Object.create(LightModule.Light.prototype);
HemisphereLight.prototype.constructor = HemisphereLight;
HemisphereLight.prototype.clone = function () {
	var light = new HemisphereLight();
	LightModule.Light.prototype.clone.call(this, light);
	light.groundColor.copy(this.groundColor);
	light.intensity = this.intensity;
	return light;
};
HemisphereLight.prototype.isHemisphereLight = true;
exports.HemisphereLight = HemisphereLight;