var ColorModule = require('../math/Color');
var Object3DModule = require('../core/Object3D');
var Light;
Light = function (color) {
	Object3DModule.Object3D.call(this);
	this.type = 'Light';
	this.color = new ColorModule.Color(color);
};
Light.prototype = Object.create(Object3DModule.Object3D.prototype);
Light.prototype.constructor = Light;
Light.prototype.clone = function (light) {
	if (light === undefined)
		light = new Light();
	Object3DModule.Object3D.prototype.clone.call(this, light);
	light.color.copy(this.color);
	return light;
};
Light.prototype.isLight = true;
exports.Light = Light;