var LightModule = require('./Light');
var PointLight;
PointLight = function (color, intensity, distance, decay) {
	LightModule.Light.call(this, color);
	this.type = 'PointLight';
	this.intensity = intensity !== undefined ? intensity : 1;
	this.distance = distance !== undefined ? distance : 0;
	this.decay = decay !== undefined ? decay : 1;	// for physically correct lights, should be 2.
};
PointLight.prototype = Object.create(LightModule.Light.prototype);
PointLight.prototype.constructor = PointLight;
PointLight.prototype.clone = function () {
	var light = new PointLight();
	LightModule.Light.prototype.clone.call(this, light);
	light.intensity = this.intensity;
	light.distance = this.distance;
	light.decay = this.decay;
	return light;
};
PointLight.prototype.isPointLight = true;
exports.PointLight = PointLight;