var LoadingManager;
LoadingManager = function (onLoad, onProgress, onError) {
	var scope = this;
	var loaded = 0, total = 0;
	this.onLoad = onLoad;
	this.onProgress = onProgress;
	this.onError = onError;
	this.itemStart = function (url) {
		total++;
	};
	this.itemEnd = function (url) {
		loaded++;
		if (scope.onProgress !== undefined) {
			scope.onProgress(url, loaded, total);
		}
		if (loaded === total && scope.onLoad !== undefined) {
			scope.onLoad();
		}
	};
};
exports.DefaultLoadingManager = new LoadingManager();
LoadingManager.prototype.isLoadingManager = true;
exports.LoadingManager = LoadingManager;
if (typeof exports.DefaultLoadingManager === 'function')
	exports.DefaultLoadingManager.prototype.isDefaultLoadingManager = true;