var ShaderMaterialModule = require('./ShaderMaterial');
var RawShaderMaterial;
RawShaderMaterial = function (parameters) {
	ShaderMaterialModule.ShaderMaterial.call(this, parameters);
	this.type = 'RawShaderMaterial';
};
RawShaderMaterial.prototype = Object.create(ShaderMaterialModule.ShaderMaterial.prototype);
RawShaderMaterial.prototype.constructor = RawShaderMaterial;
RawShaderMaterial.prototype.clone = function () {
	var material = new RawShaderMaterial();
	ShaderMaterialModule.ShaderMaterial.prototype.clone.call(this, material);
	return material;
};
RawShaderMaterial.prototype.isRawShaderMaterial = true;
exports.RawShaderMaterial = RawShaderMaterial;