var Object3DModule = require('../core/Object3D');
var Bone;
Bone = function (skin) {
	Object3DModule.Object3D.call(this);
	this.type = 'Bone';
	this.skin = skin;
};
Bone.prototype = Object.create(Object3DModule.Object3D.prototype);
Bone.prototype.constructor = Bone;
Bone.prototype.isBone = true;
exports.Bone = Bone;