var WebGLRenderTargetModule = require('./WebGLRenderTarget');
var WebGLRenderTargetCube;
WebGLRenderTargetCube = function (width, height, options) {
	WebGLRenderTargetModule.WebGLRenderTarget.call(this, width, height, options);
	this.activeCubeFace = 0;	// PX 0, NX 1, PY 2, NY 3, PZ 4, NZ 5
};
WebGLRenderTargetCube.prototype = Object.create(WebGLRenderTargetModule.WebGLRenderTarget.prototype);
WebGLRenderTargetCube.prototype.constructor = WebGLRenderTargetCube;
WebGLRenderTargetCube.prototype.isWebGLRenderTargetCube = true;
exports.WebGLRenderTargetCube = WebGLRenderTargetCube;