var Object3DModule = require('../core/Object3D');
var Scene;
Scene = function () {
	Object3DModule.Object3D.call(this);
	this.type = 'Scene';
	this.fog = null;
	this.overrideMaterial = null;
	this.autoUpdate = true;	// checked by the renderer
};
Scene.prototype = Object.create(Object3DModule.Object3D.prototype);
Scene.prototype.constructor = Scene;
Scene.prototype.clone = function (object) {
	if (object === undefined)
		object = new Scene();
	Object3DModule.Object3D.prototype.clone.call(this, object);
	if (this.fog !== null)
		object.fog = this.fog.clone();
	if (this.overrideMaterial !== null)
		object.overrideMaterial = this.overrideMaterial.clone();
	object.autoUpdate = this.autoUpdate;
	object.matrixAutoUpdate = this.matrixAutoUpdate;
	return object;
};
Scene.prototype.isScene = true;
exports.Scene = Scene;