var TextureModule = require('./Texture');
var DataTexture;
DataTexture = function (data, width, height, format, type, mapping, wrapS, wrapT, magFilter, minFilter, anisotropy) {
	TextureModule.Texture.call(this, null, mapping, wrapS, wrapT, magFilter, minFilter, format, type, anisotropy);
	this.image = {
		data: data,
		width: width,
		height: height
	};
};
DataTexture.prototype = Object.create(TextureModule.Texture.prototype);
DataTexture.prototype.constructor = DataTexture;
DataTexture.prototype.clone = function () {
	var texture = new DataTexture();
	TextureModule.Texture.prototype.clone.call(this, texture);
	return texture;
};
DataTexture.prototype.isDataTexture = true;
exports.DataTexture = DataTexture;