var TextureModule = require('./Texture');
var VideoTexture;
VideoTexture = function (video, mapping, wrapS, wrapT, magFilter, minFilter, format, type, anisotropy) {
	TextureModule.Texture.call(this, video, mapping, wrapS, wrapT, magFilter, minFilter, format, type, anisotropy);
	this.generateMipmaps = false;
	var scope = this;
	var update = function () {
		requestAnimationFrame(update);
		if (video.readyState === video.HAVE_ENOUGH_DATA) {
			scope.needsUpdate = true;
		}
	};
	update();
};
VideoTexture.prototype = Object.create(TextureModule.Texture.prototype);
VideoTexture.prototype.constructor = VideoTexture;
VideoTexture.prototype.isVideoTexture = true;
exports.VideoTexture = VideoTexture;